import "./App.css";
import Home from "./resources/home/home.jsx";
import login from "./resources/logsignup/login.jsx";
import signup_business from "./resources/logsignup/signup_business.jsx";
import signup_personal from "./resources/logsignup/signup_personal.jsx";
import dashboard from './resources/dashboard/dashboard'
import account from './resources/grid/account/account'
import administrate_account from './resources/grid/editaccount/administrate_account'
import explore from './resources/grid/explore/explore'
import accounthrd from './resources/grid/account/accounthrd'
import matchmaking from './resources/matchmaking'
import { useEffect, useState } from "react";
import logo from "./.files/logo.png";
import AOS from "aos";
import Nav from './resources/navbar'
import "aos/dist/aos.css";
import { Route, Router } from "react-router-dom";
import $ from 'jquery'
import $$ from 'jquery.cookie'
function App() {
    const [loaded, setloaded] = useState(false);

    useEffect(() => {

        AOS.init();
    }, []);
    if (loaded == true || window.location.pathname != "/") {
        if(localStorage.getItem('token')){
            if(window.location.pathname=='/' || window.location.pathname=='/signin' || window.location.pathname=='/signup-business' || window.location.pathname=='/signup-personal' || window.location.pathname=='/signin'){
           return( <div>
            <Route exact path = "/"
            component = { Home }/>
             <Route exact path = "/signin"
            component = { login }/>
             <Route exact path = "/signup-business"
            component = { signup_business }/>
             <Route exact path = "/signup-personal"
            component = { signup_personal }/>
            <Route exact path = "/signin"
            component = { login }/> 
           </div>)

        }else{
            return( <div>
            <Nav/>
            <Route exact path = '/dashboard'
            component = { dashboard }/>
            <Route exact path ='/account'
            component= {account}/>
            <Route exact path ='/administrate-account'
            component ={administrate_account}/>
            <Route exact path='/explore'
            component={explore}/>
            <Route exact path={`/${$.cookie('httpurl')}`}
                component={accounthrd}/>
               <Route exact path='/matchmaking'
                   component={matchmaking}/>
           </div>)
        }
        }
            if(window.location.pathname != '/signin' && window.location.pathname != '/' && window.location.pathname != '/signup-business' && window.location.pathname != '/signup-personal'){
               window.location.pathname='/signin'
            }
            return ( <div>
                <Route exact path = "/"
                component = { Home }/>
                 <Route exact path = "/signin"
                component = { login }/>
                 <Route exact path = "/signup-business"
                component = { signup_business }/>
                 <Route exact path = "/signup-personal"
                component = { signup_personal }/>
                 </div>
            );   

    } else if (loaded == false && window.location.pathname == "/") {
        setTimeout(() => {
            setloaded(true);
        }, 2520);
        return ( <div className = "load-screen-d" >
            <div data-aos = "fade-up"
            data-aos-duration = "500" >
            <i class = "bx bx-shape-triangle" id = "logo-shape-loading" > </i> 
            <br/>
            <progress value = "1"
            max = "1"
            id = "loader-screen" />
            </div> <div id = "loading-screen-logo"
            data-aos = "zoom-in-up"
            data-aos-duration = "1000" >
            <h5 id = "loading-from" > FROM </h5> <img src = { logo }
            id = "logo-footer" />
            <img src = { require(`./.files/evercode-logo.png`) }
            id = "logo-text-footer" />
            </div> </div>
        );
    }
}

export default App;
