import axios from 'axios'
import { useEffect, useState } from 'react'
import './navbar.scss'
import $ from 'jquery'
import { Link } from 'react-router-dom'
import { useScrollPosition } from '@n8tb1t/use-scroll-position'

const Nav = () =>{
    const [data,setData]=useState({
    })
	const [pic,setPic]=useState('default-profile-pic.png')
	const [banner,setBanner]=useState('default-profile-banner.png')
	const [name,setName]=useState('');
	const [datatype,setDataType]=useState('')
    const [searchResults,setSearchResults]=useState([])
    const onchangesearch = async e =>{
            await axios.post(`${process.env.REACT_APP_HOST}/api/search-results`,{
                searchfilter:e.target.value,
                email:data.email
            }).then((reqsf)=>{
                if(reqsf.data.searchresults){
                 setSearchResults(reqsf.data.searchresults);
                }
            }) 
			            if(e.target.value.length === 0){
							$("#Modal").fadeOut();
						}else{
							$("#Modal").fadeIn();
							$('#notification-modal').fadeOut(0);
							$('#settings-modal').fadeOut(0)
						}
    }
    useEffect(()=>{
        axios.post(`${process.env.REACT_APP_HOST}/api/get-user-data`,
        { },{ headers: {'Authorization': `${localStorage.getItem('token')}`} })
        .then((req)=>{
            if(req.data.data){
                setData(req.data.data);
				if(req.data.data.type === 'personal'){
					setDataType('Personal')

				}else{
					setDataType('Business')
				}
				setName(req.data.data.name)
				setPic(req.data.data.pic)
				setBanner(req.data.data.banner)
            }
        })
    }, [])
	useScrollPosition(({ prevPos, currPos }) => {
		if(currPos.y <= -1){
			$('.home-nav').css({boxShadow:'1px 1px 2px .5px rgba(0,0,0,0.15)',
								background:'rgba(255,255,255,.75)'})
		}else{
			$('.home-nav').css({boxShadow:'1px 1px 2px .5px rgba(0,0,0,0)',
								background:'transparent'})
		}
	   })
    return(
<nav className='home-nav' id='nav-nav'>
    <div id='left-nav-side'>
		<Link to='/dashboard'><span id='span-logo1' style={{cursor:'pointer'}}>
        <i class='bx bx-shape-triangle'  style={{float:'none', marginLeft:'4em'}} data-aos="zoom-in-right" id='logo-shape'></i>
		</span> 
			<h3 id='logo-text'  class='nav-logo-text' style={{float:'none',cursor:'pointer'}} data-aos="zoom-in-up">in<span id='half-blue'>Work</span></h3></Link>
            <div class="searchbox">
		<button class="btn-menu">
		<i class='bx bx-shape-triangle' style={{float:'none'}} data-aos="zoom-in-right" id='logo-shape'></i>

		</button>
        <form id='form-nav'>
            <input id="search"   autoComplete='off' onChange={(e)=>{onchangesearch(e)}} type="text" placeholder="Search..." name="search"
            class="search"/> </form>
            <button  class="btn-search">
	<i class="gg-search" style={{transform:'scale(.85)',marginRight:'.15em'}}></i>
			</button>
</div>
<div id='notification-modal' class="search-modal">
		<div class="search-modal-header">
				<h3 style={{fontSize:'.95em',padding:'.25em'}}>
				<i class="gg-loadbar-doc" id='grid' style={{display:'inline-block',marginRight:'.5em',marginTop:'.08em',transform:'scale(1.01)'}}></i>
				Notifications</h3>
				<span id="close" onClick={()=>{
												$("#notification-modal").fadeOut();
				}}><i class='bx bx-x'></i></span>
		</div>
		<div class="search-modal-body">
			<div class="message">
					<div class="message-avatar">
							<img src="https://randomuser.me/api/portraits/women/57.jpg" alt=""/>
					</div>
				<div class="message-body">
						<p>New notification from kate morrison.</p>
					<small>20s ago</small>
				</div>
			</div>
		</div>
</div>
<div id='settings-modal' class="search-modal">
		<div class="search-modal-header">
				<h3 style={{fontSize:'.95em',padding:'.25em'}}>
				<i class='bx bxs-grid' id='grid'></i>Grid</h3>
				<span id="close" onClick={()=>{
												$("#settings-modal").fadeOut();
				}}><i class='bx bx-x'></i></span>
		</div>
		<div class="search-modal-body">
		<img src={ require(`../.files/database/${banner}`)} id='nav-user-banner-modal'/>
		<img src={ require(`../.files/database/${pic}`) } className='profile-pic' id='nav-user-pic-modal'/>
		<h3 id='am-fullname'>{name}</h3>
		<h3 id='am-profession'>{data.profession} (<i class='bx bxs-star' id='star-small'></i><span id='stars-t'>{data.stars}</span>)</h3>
		<br/>
		<div class='administrate-account-cards'>
			<Link to='/account'><div className='card' onClick={()=>{
				$('.search-modal').fadeOut()
			}} onMouseOver={()=>{
				$('#ct1').css({color:'rgb(125,125,125)'})
			}} onMouseOut={()=>{
				$('#ct1').css({color:'white'})
			}}>
			<img src={require(`../.files/database/${pic}`)} id='nav-user-pic' style={{margin:0}} className='card-svg'/>
	<br/>		<h5 className='card-text' id='ct1'>Account</h5>
			</div></Link>
			<Link to='/explore'>
			<div className='card' onClick={()=>{
				$('.search-modal').fadeOut()
			}} onMouseOver={()=>{
				$('#ct2').css({color:'rgb(125,125,125)'})
			}} onMouseOut={()=>{
				$('#ct2').css({color:'white'})
			}}>
			<i class='bx bx-compass' id='settings-bx'></i>
			<h5 className='card-text' id='ct2'>Explore</h5>
			</div></Link>
			<Link to='/administrate-account'>
			<div className='card' onClick={()=>{
				$('.search-modal').fadeOut()
			}} onMouseOver={()=>{
				$('#ct3').css({color:'rgb(125,125,125)'})
			}} onMouseOut={()=>{
				$('#ct3').css({color:'white'})
			}}>
			<i class='bx bxs-cog' id='settings-bx'></i>
			<h5 className='card-text' id='ct3'>Edit Account</h5>
			</div></Link>
			<span id='signout-span' onClick={()=>{
				localStorage.clear();
				window.location.pathname='/signin'
			}}>
			<i class='bx bx-exit' id='signout-i-nav'></i>
				<h4 id='signout-t-nav'>Sign Out</h4>
			</span>
		</div>
		</div>

</div>

<div id="Modal" class="search-modal">
		<div class="search-modal-header">
				<h3 style={{fontSize:'.95em',padding:'.25em'}}>
				<i class='bx bx-search' id='grid'></i>Search results</h3>
				<span id="close" onClick={()=>{
												$("#Modal").fadeOut();
				}}><i class='bx bx-x'></i></span>
		</div>
    <div className='search-results-father'>
    {searchResults.map((sr)=>{
        let name;
        let pic ='default-business-pic.png'
        if(sr.type == 'personal'){
            name = sr.name + ' ' + sr.lastname;
            pic = sr.profile_pic;
        }
        if(sr.type == 'business'){
            name = sr.business_name;
            pic = sr.business_pic;
        }
        return(
<div class="search-modal-body">
			<div class="message">
					<div class="message-avatar">
							<img src={require(`../.files/database/${pic}`)}  alt=""/>
					</div>
				<div class="message-body">
						<p>{name}</p>
					<small>               <p id='location-t-c' style={{float:'left',marginTop:'2em',marginLeft:'1em',fontSize:'.9em',color:'rgb(120,120,120)'}}>
                                  <i class='bx bxs-map' style={{fontSize:'.95em'}} id='map-location-bx'></i>{sr.location}
                    </p><p id='sr-prf'>{sr.profession}</p></small>
				</div>
			</div>
		</div>
        )
    })}	
    </div>

</div>
            </div>
            <div id='right-nav-side'>
			<svg 
							 id='notification-nav' onClick={()=>{
								$('#notification-modal').fadeIn()
								$('#Modal').fadeOut(0);
								$('#settings-modal').fadeOut(0)
							}} style={{display:'inline-block',transform:'scale(1.25)',verticalAlign:'bottom',marginRight:'.62em'}}
			xmlns="http://www.w3.org/2000/svg" width="33" height="31" fill="none" stroke="#000000" stroke-width="1" stroke-linecap="round" stroke-linejoin="round">&lt;!--!  Atomicons Free 1.00 by @atisalab License - https://atomicons.com/license/ (Icons: CC BY 4.0) Copyright 2021 Atomicons --&gt;<path d="M19.2,14.27l1.6,2.13A1,1,0,0,1,20,18H4a1,1,0,0,1-.8-1.6l1.6-2.13a1,1,0,0,0,.2-.6V9.29A7.2,7.2,0,0,1,11.78,2,7,7,0,0,1,19,9v4.67A1,1,0,0,0,19.2,14.27Z"></path><path d="M16,18a4,4,0,1,1-8,0"></path></svg>			
                        <img src={ require(`../.files/database/${pic}`) } onClick={()=>{
				$('#settings-modal').fadeIn()
				$('#notification-modal').fadeOut(0);
				$('#Modal').fadeOut(0)
			}} className='profile-pic' id='nav-user-pic'/>

                        <button className='match-b' id='login-but' style={{float:'right',marginTop:'.8em'}} data-aos="zoom-in-left" onClick={()=>{alert(data.pic)}}><i class="gg-search" id='match-search' style={{marginLeft:'.0em',marginRight:'.2em',display:'inline-block',transform:'scale(.67)',color:'white'}}></i>Match</button>
						<div className='identify-nav'>
				<h5 id='type-account-text-nav'>{datatype}</h5>
				<h5 id='name-account-text-nav'>{name}</h5>
			</div>
					    </div>
        </nav>
    )
} 
export default Nav;
