import './dashboard.scss'
import './cards.scss'
import Nav from '../navbar'
import { useEffect,useState } from 'react'
import axios from 'axios'
import $ from 'jquery'
const Dashboard = () =>{
    const [data,setData]=useState({
        banner:'default-profile-banner.png',
        pic:'default-profile-pic.png'
    })
    const [users,setUsers]=useState([])
    const [userData,setUserData]=useState([])

    const getUserData = (user) =>{
        axios.post(`${process.env.REACT_APP_HOST}/api/get-user-data-dashboard`,{
            user:user
        }).then((req)=>{
            if(req.data.userdata){
              setUserData(req.data.userdata); 
            } })   
    }
    useEffect(()=>{
        axios.post(`${process.env.REACT_APP_HOST}/api/get-user-data`,
            {  }, { headers: {'Authorization': `${localStorage.getItem('token')}`} }).then((req)=>{
                if(req.data.data){
                    setData(req.data.data);
                    if(req.data.data.type=='personal'){
                        $('.dashboard-personal-body').fadeIn();
                    }
                    if(req.data.data.type=='business'){
                        $('.dashboard-business-body').fadeIn();
    axios.post(`${process.env.REACT_APP_HOST}/api/get-preferences-dashboard`,{
                            email:req.data.data.email
                        }).then((req2)=>{
                            if(req2.data.prefs){
            axios.post(`${process.env.REACT_APP_HOST}/api/get-personal-users`,
                            {
                                email:req.data.data.email,
                                sf1:req2.data.prefs.searchingfor1,
                                sf2:req2.data.prefs.searchingfor2,
                                sf3:req2.data.prefs.searchingfor3,
                                sk1:req2.data.prefs.searchingskill1,
                                sk2:req2.data.prefs.searchingskill2,
                                sk3:req2.data.prefs.searchingskill3
                            }).then((req_user)=>{
                                if(req_user.data.users){
                                    setUsers(req_user.data.users);
                                }
                            })


                            }
                        })
                    }
                }
            })
    },[])
    return(
        <div class='dashboard-body'>
            <div class='dashboard-personal-body'>
                  personal body
            </div>
            <div class='dashboard-business-body'>
            <h1 id='dashboard-title-business'>Based on your Preferences</h1>   
                <div class='personal-users-dashboard'>
                    {users.map((user,i)=>{
                        getUserData(user.email)
                        return(
<div class='card-dash'> 
                          <img src={require(`../../.files/database/${data.banner}`)} id='imgcard'/>
                          <div class='card-content'>
                            <img src={require(`../../.files/database/${data.pic}`)} id='imgcard-pic'/>
                            <h3 id='card-name'>{data.name}</h3>
                            <h5 id='card-profession'>{data.profession}</h5>

                          </div>
                          </div>
                    )
                    })}

                </div>
            </div>
        </div>
    )
}
export default Dashboard;
