import axios from 'axios'
import { useEffect, useState } from 'react'
import './navbar.scss'
import $ from 'jquery'
import matchsvg from '../.files/match.svg'
import { Link } from 'react-router-dom'
import { useScrollPosition } from '@n8tb1t/use-scroll-position'
import $$ from 'jquery.cookie'
import Swal from 'sweetalert2'
const Nav = () =>{
    const [data,setData]=useState({
    })
    const [matchtitle, setMatchtitle]=useState('')
    const [matchsubtitle, setMatchsubtitle]=useState('')
	const [pic,setPic]=useState('default-profile-pic.png')
	const [banner,setBanner]=useState('default-profile-banner.png')
	const [name,setName]=useState('');
	const [datatype,setDataType]=useState('')
    const [searchResults,setSearchResults]=useState([])
    const [searchFilter,setSearchFilter]=useState('')
    const [sr,setSr]=useState([])
    const [notf,setNotf]=useState([])
    const [starsupd,setstarupd]=useState(0)
    const [inQeue, setInQeue]=useState(false)
    useEffect(()=>{
        const interval = setInterval(() => {
            axios.post(`${process.env.REACT_APP_HOST}/api/checknotf`,{
                email:data.email
            }).then((reqnotf)=>{
                if(reqnotf.data.has){
                    $('#circle-notification-bell').fadeIn();
                }
            }) 
  }, 1000)
  return () => clearInterval(interval)
    })
    useEffect(()=>{
        const interval = setInterval(() => {
        if(inQeue === true){
            axios.post(`${process.env.REACT_APP_HOST}/api/f-m`,{
                email:data.email,
                type:data.type
            }).then((reqfm)=>{
                if(reqfm.data.isfinded){
                             $('#searching-match-c').fadeOut(0)
                            $('#founded-match-c').fadeIn()
                }
            })
        }
  }, 1500)
  return () => clearInterval(interval)

    })
    const closem = () =>{
        setInQeue(false)
        $('.match-modal').fadeOut(0)
        $('#start-match-c').fadeIn(0)
        $('#searching-match-c').fadeOut(0)
        $('#founded-match-c').fadeOut(0)  
    }
    const startsearching = () =>{
                setInQeue(true)
             $('#start-match-c').fadeOut(0);
             $('#searching-match-c').fadeIn(); 
             axios.post(`${process.env.REACT_APP_HOST}/api/startqeue`,{
                    email:data.email,
                    type:data.type,
                    profession:data.profession
              })

    }
    useEffect(()=>{
        axios.post(`${process.env.REACT_APP_HOST}/api/get-user-data`,
        { },{ headers: {'Authorization': `${localStorage.getItem('token')}`} })
        .then((req)=>{
            if(req.data.data){
                setData(req.data.data);
                axios.post(`${process.env.REACT_APP_HOST}/api/checknotf`, {
                    email:req.data.data.email
                }).then((reqcn)=>{
                    if(reqcn.data.has){
                        $('#circle-notification-bell').fadeIn()
                    }
                })
            axios.post(`${process.env.REACT_APP_HOST}/api/rmbothq`,{
                email:req.data.data.email,
                type:req.data.data.type
            })
                axios.post(`${process.env.REACT_APP_HOST}/api/get-notf`,{
                    email:req.data.data.email
                }).then((reqnotf)=>{
                    if(reqnotf.data.notf){
                        setNotf(reqnotf.data.notf)
                        $('.notf-content').fadeIn()
                    }else if(reqnotf.data.undefinednotf){
                        $('.undefinednotf-content').fadeIn()
                    }
                })
                axios.post(`${process.env.REACT_APP_HOST}/api/search-results`,{
                    email:req.data.data.email
                }).then((reqsr)=>{
                    if(reqsr.data.sr){
                        setSearchResults(reqsr.data.sr)
                    }
                })
				if(req.data.data.type === 'personal'){
					setDataType('Personal')
                    setMatchtitle('Search for recruiters')
                    setMatchsubtitle(`You will search recruiters that are searching for ${req.data.data.profession}`)
				}else{
					setDataType('Business')
                    setMatchtitle(`Start Recruiting`)
                    setMatchsubtitle(`You will search for personal based on your business preferences`)
				}
				setName(req.data.data.name)
				setPic(req.data.data.pic)
				setBanner(req.data.data.banner)
            }
        })
    }, [])
	useScrollPosition(({ prevPos, currPos }) => {
		if(currPos.y <= -1){
			$('.home-nav').css({boxShadow:'1px 1px 2px .5px rgba(0,0,0,0.15)',
								background:'rgba(255,255,255,.75)'})
		}else{
			$('.home-nav').css({boxShadow:'1px 1px 2px .5px rgba(0,0,0,0)',
								background:'transparent'})
		}
	   })
    return(
<nav className='home-nav' id='nav-nav'>
    <div id='left-nav-side'>
		<Link to='/dashboard'><span id='span-logo1' style={{cursor:'pointer'}}>
        <i class='bx bx-shape-triangle'  style={{float:'none', marginLeft:'4em'}} data-aos="zoom-in-right" id='logo-shape'></i>
		</span> 
			<h3 id='logo-text'  class='nav-logo-text' style={{float:'none',cursor:'pointer'}} data-aos="zoom-in-up">in<span id='half-blue'>Work</span></h3></Link>
            <div class="searchbox">
		<button class="btn-menu">
            <i class='bx bx-shape-triangle' onClick={()=>{
            window.location.pathname = '/dashboard'
            }} style={{float:'none'}} data-aos="zoom-in-right" id='logo-shape'></i>

		</button>
        <form id='form-nav'>
            <input id="search"  autoComplete='off' onChange={(e)=>{
                setSearchFilter(e.target.value)		
                if(e.target.value.length === 1 || e.target.value.length === 0){
							$("#Modal").fadeOut();
						}else{
							$("#Modal").fadeIn();
							$('#notification-modal').fadeOut(0);
							$('#settings-modal').fadeOut(0)
						}
                }} type="text" placeholder="Search..." name="search"
            class="search"/> </form>
            <button  class="btn-search">
                <i class="gg-search" style={{cursor:'default',transform:'scale(.85)',marginRight:'.15em'}}></i>
			</button>
</div>
<div class='match-modal'>
    <div class='match-modal-content'>
        <p class='close-m' onClick={closem}>
                </p>
        <div id='start-match-c'>
                   <img src={matchsvg} id='match-svg'/>
        <h3 id='match-title'>
            {matchtitle} 
        </h3>
        <h5 id='match-subtitle'> 
            {matchsubtitle}
        </h5>
            <button id='start-searching-match' onClick={startsearching}>
            Start Searching
        </button>  
        </div>
        <div id='searching-match-c'>
            <i class="gg-loadbar" id='searching-c'></i>
            <h3 id='sfbm'>
                 Searching for best match... be patient
            </h3>
        </div>
        <div id='founded-match-c'>
            founded match c
        </div>
    </div>
</div>
<div id='notification-modal' class="search-modal">
		<div class="search-modal-header">
				<h3 style={{fontSize:'.95em',padding:'.25em'}}>
				<i class="gg-loadbar-doc" id='grid' style={{display:'inline-block',marginRight:'.5em',marginTop:'.08em',transform:'scale(1.01)'}}></i>
				Notifications</h3>
				<span id="close" onClick={()=>{
                    $("#notification-modal").fadeOut();
            }}><i class='close-m'></i></span>
		</div>
    <div className='undefinednotf-content'>
        Undefined 
    </div>
            <div className='notf-content'>
    {notf.map((nf)=>{
        return(
            <div class="search-modal-body" onClick={()=>{
                if(nf.notftitle == `${nf.fname} has modify your stars.`){
                    window.location.pathname = '/account'
                }else if(nf.notftitle == `${nf.fname} has requested you to match!`){
                    Swal.fire({
  title: `You want to accept the request match from ${nf.fname}?`,
  text: "if you decline the request, you can accept it later!",
  icon: 'warning',
  iconColor:'#666',
padding:'60px',
  showCancelButton: true,
  confirmButtonColor: '#24252A',
  cancelButtonColor: 'rgb(235,235,235)',
  cancelButtonText: '<p>Decline Request</p>',
  confirmButtonText: 'Accept Request'
}).then((result) => {
    if (result.isConfirmed) {
        axios.post(`${process.env.REACT_APP_HOST}/api/match`,{
            from:nf.fr0m,
            to:nf.t0
        },{headers:{'authorization':`${localStorage.getItem('token')}`}})
      Swal.fire({
          title:`Accepted Request!`,
          icon:'success',
          text:`You has matched with ${nf.fname}`,
          confirmButtonColor:'#24252A'
      })
  }
})
                }
                }}>
			<div class="message">
					<div class="message-avatar">
							<img src={require(`../.files/database/${nf.pic}`)} alt=""/>
					</div>
				<div class="message-body">
						<p>{nf.notftitle}</p>
					<small>{nf.date}</small>
				</div>

			</div>
                <span id='i' class='type-notf' style={{marginTop:'-2.35em'}}>
                        {nf.type.toUpperCase()[0]}{nf.type.slice(1)} 
                    </span>
		</div>
        )
    })}
    </div>
</div>
<div id='settings-modal' class="search-modal">
		<div class="search-modal-header">
				<h3 style={{fontSize:'.95em',padding:'.25em'}}>
				<i class='bx bxs-grid' id='grid'></i>Grid</h3>
				<span id="close" onClick={()=>{
												$("#settings-modal").fadeOut();
				}}><i class='close-m'></i></span>
		</div>
		<div class="search-modal-body">
		<img src={ require(`../.files/database/${banner}`)} id='nav-user-banner-modal'/>
		<img src={ require(`../.files/database/${pic}`) } className='profile-pic' id='nav-user-pic-modal'/>
		<h3 id='am-fullname'>{name}</h3>
		<h3 id='am-profession'>{data.profession} (<i class='bx bxs-star' id='star-small'></i><span id='stars-t'>{starsupd}</span>)</h3>
		<br/>
		<div class='administrate-account-cards'>
			<Link to='/account'><div className='card' onClick={()=>{
				$('.search-modal').fadeOut()
			}} onMouseOver={()=>{
				$('#ct1').css({color:'rgb(125,125,125)'})
			}} onMouseOut={()=>{
				$('#ct1').css({color:'white'})
			}}>
			<img src={require(`../.files/database/${pic}`)} id='nav-user-pic' style={{margin:0}} className='card-svg'/>
	<br/>		<h5 className='card-text' id='ct1'>Account</h5>
			</div></Link>
			<Link to='/explore'>
			<div className='card' onClick={()=>{
				$('.search-modal').fadeOut()
			}} onMouseOver={()=>{
				$('#ct2').css({color:'rgb(125,125,125)'})
			}} onMouseOut={()=>{
				$('#ct2').css({color:'white'})
			}}>
			<i class='bx bx-compass' id='settings-bx'></i>
			<h5 className='card-text' id='ct2'>Explore</h5>
			</div></Link>
			<Link to='/administrate-account'>
			<div className='card' onClick={()=>{
				$('.search-modal').fadeOut()
			}} onMouseOver={()=>{
				$('#ct3').css({color:'rgb(125,125,125)'})
			}} onMouseOut={()=>{
				$('#ct3').css({color:'white'})
			}}>
			<i class='bx bxs-cog' id='settings-bx'></i>
